		echo "<a href=\"payments_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: payments_after_insert
	if(function_exists('payments_after_insert')){
		$res = sql("select * from `payments` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
